using System;
using Xunit;
using valutebank;
using valutebank.DOMAIN;

namespace TestProject1
{
    public class NoteTests
    {

        [Fact]
        public void emptynote()
        {
            User u = new User("test");
            MainController.AddNote(u,"");
            Assert.Matches("",u.History[0]);
        }

        [Theory]
        //71к символов
        [InlineData("")]
        //
        public void bignote(string value)
        {
            User u = new User("test");
            MainController.AddNote(u, value);
            Assert.Matches(value, u.History[0]);
        }

        [Fact]
        public void nullnote()
        {
            User i = new User("test1");
            MainController.AddNote(i, null);
            Assert.Null(i.History[0]);
        }

        [Fact]

        public void funcnote()
        {
            User i = new User("test1");
            User j = new User("test2");
            string note = i.Name + " перевел 999 с btc юзеру " + j.Name + " на eth ";
            MainController.AddNote(i, note);
            Assert.Matches(note, i.History[0]);
        }

    }
}
